import React, {Fragment, DataGrid } from 'react';
import { Table } from 'react-bootstrap';
import { PDFViewer, PDFDownloadLink, Page, Text, View, Document, StyleSheet } from '@react-pdf/renderer';


// Create styles
const borderColor = '#90e5fc'

const styles = StyleSheet.create({
  document: {
    width: '100%',
    height: '100% !important'
  },  
  table:{ 
    textAlign: 'center',
    
  },   
  header:{ 
    textAlign: 'center',
    margin: 20
    
  }, 
  headertable:{ 

    flexDirection: 'row',   
    fontSize: 12,  
    marginLeft: 70 
    
  },
  page: {  
    backgroundColor: '#FFFFFF',
    width: '100%',
    height: '100% !important'
  },
  section: {
    width: 500,      
    height: 100,
    margin: 10,
    padding: 10    
  },
  invoiceNoContainer: {
    flexDirection: 'row',
    marginTop: 36,
    justifyContent: 'flex-end'
  },
  tableContainer: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        marginTop: 24,
        borderWidth: 1,
        borderColor: '#bff0fd',
  },
  container: {
    flexDirection: 'row',
    borderBottomColor: '#bff0fd',
    backgroundColor: '#bff0fd',
    borderBottomWidth: 1,
    alignItems: 'center',
    height: 24,
    textAlign: 'center',
    fontStyle: 'bold',
    flexGrow: 1,
},
description: {
    width: '60%',
    borderRightColor: borderColor,
    borderRightWidth: 1,
},
qty: {
    width: '10%',
    borderRightColor: borderColor,
    borderRightWidth: 1,
},
rate: {
    width: '15%',
    borderRightColor: borderColor,
    borderRightWidth: 1,
},
amount: {
    width: '15%'
}
});

const Report = (props) => {
      
  
  let totalExportacoes = props.data.movimentacoes ? props.data.movimentacoes.totalExportacao : 0
  let totalImportacoes = props.data.movimentacoes ? props.data.movimentacoes.totalImportacao : 0


  var rows = props.data.movimentacoes ? props.data.movimentacoes.movimentacoes.map((m, index) => {
    
    return m.map((mov,i) => {
     
      return  <View style={{ flexDirection: 'row'}} key={i}>
                <Text style={styles.row}>{mov.conteiner.cliente.nome}</Text>
                <Text style={styles.row}>{mov.conteiner.numero}</Text>
                <Text style={styles.row}>{mov.tipoMovimentacao.descricao}</Text>
                <Text style={styles.row}>{mov.conteiner.categoria.descricao}</Text>
                <Text style={styles.row}>{mov.dataHoraInicio}</Text>
                <Text style={styles.row}>{mov.dataHoraFim}</Text>
              </View>
    })

  }) : []

  return (
    <div>
     <PDFViewer style={styles.document} height={745} >
            <Document >
          <Page size="A4" style={styles.page}>
            {/* <View style={styles.header}>
              <Text>T2S - Movimentações</Text>
            </View> 
            <View style={styles.headertable}>
              <Text style={{ margin: 8}}>Cliente</Text>
              <Text style={{ margin: 8}}>Numero</Text>
              <Text style={{ margin: 8}}>Tipo de Movimentação</Text>
              <Text style={{ margin: 8}}>Categoria</Text>
              <Text style={{ margin: 8}}>Data Inicio</Text>
              <Text style={{ margin: 8}}>Data Fim</Text>
            </View>    */}
            
            <View style={styles.container}>
                <Text style={styles.description}>Item Description</Text>
                <Text style={styles.qty}>Qty</Text>
                <Text style={styles.rate}>@</Text>
                <Text style={styles.amount}>Amount</Text>
            </View>
              {/* <View style={styles.tableContainer}>
                <InvoiceTableHeader />
                <InvoiceTableRow items={rows} />
                <InvoiceTableBlankSpace rowsCount={ rows.rowsCount - invoice.items.length} />
                <InvoiceTableFooter items={invoice.items} />
             </View> */}
           
           <View style={styles.section}>
              <Text>Total de Exportações: {totalExportacoes}</Text>         
              <Text>Total de Importações: {totalImportacoes}</Text>
           </View>                              
          </Page>
        </Document>
      </PDFViewer>                                                                               
    </div>
    )            
}

export default Report
