import React, {Fragment, DataGrid } from 'react';
import { Table } from 'react-bootstrap';
import { PDFViewer, PDFDownloadLink, Page, Text, View, Document, StyleSheet } from '@react-pdf/renderer';


// Create styles
const borderColor = '#90e5fc'

const styles = StyleSheet.create({
  document: {
    width: '100%',
    height: '100% !important'
  },  
  table:{ 
    textAlign: 'center',
    
  },   
  header:{ 
    textAlign: 'center',
    margin: 20
    
  }, 
  headertable:{ 

    flexDirection: 'row',   
    fontSize: 12,  
    marginLeft: 70 
    
  },
  page: {  
    backgroundColor: '#FFFFFF',
    width: '100%',
    height: '100% !important'
  },
  section: {
    width: 500,      
    height: 100,
    margin: 10,
    padding: 10    
  },
  invoiceNoContainer: {
    flexDirection: 'row',
    marginTop: 36,
    justifyContent: 'flex-end'
  },
  tableContainer: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        marginTop: 24,
        borderWidth: 1,
        borderColor: '#bff0fd',
        fontSize: 12
  },
  container: {
    flexDirection: 'row',
    borderBottomColor: '#bff0fd',
    backgroundColor: '#bff0fd',
    borderBottomWidth: 1,
    alignItems: 'center',
    height: 24,
    textAlign: 'center',
    fontStyle: 'bold',
    flexGrow: 1,
},
cliente: {
    width: '15%',
    borderRightColor: borderColor,
    borderRightWidth: 1,
},
numconteiner: {
    width: '15%',
    borderRightColor: borderColor,
    borderRightWidth: 1,
},
tipomovimentacao: {
  width: '25%',
  borderRightColor: borderColor,
  borderRightWidth: 1,
},
categoria: {
  width: '15%',
  borderRightColor: borderColor,
  borderRightWidth: 1,
},
datainicio: {
  width: '15%',
  borderRightColor: borderColor,
  borderRightWidth: 1,
},
datafim: {
  width: '15%',
  borderRightColor: borderColor,
  borderRightWidth: 1,
},
row: {
  flexDirection: 'row',
  borderBottomColor: '#bff0fd',
  borderBottomWidth: 1,
  alignItems: 'center',
  height: 24,
  fontStyle: 'bold',
},
clienterow: {
  width: '60%',
  textAlign: 'left',
  borderRightColor: borderColor,
  borderRightWidth: 1,
  paddingLeft: 8,
},
numconteinerrow: {
  width: '10%',
  borderRightColor: borderColor,
  borderRightWidth: 1,
  textAlign: 'right',
  paddingRight: 8,
},
tipomovimentacaorow: {
  width: '15%',
  borderRightColor: borderColor,
  borderRightWidth: 1,
  textAlign: 'right',
  paddingRight: 8,
},
categoriarow: {
  width: '15%',
  borderRightColor: borderColor,
  borderRightWidth: 1,
  textAlign: 'right',
  paddingRight: 8,
},
datainiciorow: {
  width: '15%',
  borderRightColor: borderColor,
  borderRightWidth: 1,
  textAlign: 'right',
  paddingRight: 8,
},
datafimrow: {
  width: '15%',
  borderRightColor: borderColor,
  borderRightWidth: 1,
  textAlign: 'right',
  paddingRight: 8,
}
});




const Report = (props) => {
      
  
  let totalExportacoes = props.data.movimentacoes ? props.data.movimentacoes.totalExportacao : 0
  let totalImportacoes = props.data.movimentacoes ? props.data.movimentacoes.totalImportacao : 0


  
  var rows = props.data.movimentacoes ? props.data.movimentacoes.movimentacoes.map((m, index) => {
    return m.map((mov,i) => {
 
      return  <View style={styles.row} key={i}>
                <Text style={styles.cliente}>{mov.conteiner.cliente.nome}</Text>
                <Text style={styles.numconteiner}>{mov.conteiner.numero}</Text>
                <Text style={styles.tipomovimentacao}>{mov.tipoMovimentacao.descricao}</Text>
                <Text style={styles.categoria}>{mov.conteiner.categoria.descricao}</Text>
                <Text style={styles.datainicio}>{mov.dataHoraInicio}</Text>
                <Text style={styles.datafim}>{mov.dataHoraFim}</Text>
              </View>
    })

  }) : []


    const InvoiceTableRow = () => {
            
      return (<Fragment>{rows}</Fragment> )
    };

   

  return (
    <div>
     <PDFViewer style={styles.document} height={745} >
            <Document >
          <Page size="A4" style={styles.page}>
             <View style={styles.header}>
              <Text>T2S - Movimentações</Text>
            </View> 
           
            <View style={styles.tableContainer}>
              <View style={styles.container}>
                  <Text style={styles.cliente}>Cliente</Text>
                  <Text style={styles.numconteiner}>Num. Conteiner</Text>
                  <Text style={styles.tipomovimentacao}>Tipo de Movimentação</Text>
                  <Text style={styles.categoria}>Categoria</Text>
                  <Text style={styles.datainicio}>Data Inicio</Text>
                  <Text style={styles.datafim}>Data Fim</Text>
              </View>
              <InvoiceTableRow/>
            </View>
              {/* <View style={styles.tableContainer}>
                <InvoiceTableHeader />
                <InvoiceTableRow items={rows} />
                <InvoiceTableBlankSpace rowsCount={ rows.rowsCount - invoice.items.length} />
                <InvoiceTableFooter items={invoice.items} />
             </View> */}
           
           <View style={styles.section}>
              <Text>Total de Exportações: {totalExportacoes}</Text>         
              <Text>Total de Importações: {totalImportacoes}</Text>
           </View>                              
          </Page>
        </Document>
      </PDFViewer>                                                                               
    </div>
    )            
}

export default Report
